// define ADD, addMessage(), messageReducer(), and store here:
const ADD = 'ADD';
const addMessage = message=>{
    return {
        type: ADD, 
        message  
    }
}

const messageReducer = (address=[], action)=>{
    switch  (action.type){
        case ADD:
        return [...address,action.message];
        break; 

        default: 
        return address; 
    }
}

const store = Redux.createStore(messageReducer);
