function steamrollArray(arr) {
 const filterArray = [];
 for( let i= 0; i < arr.length; i++){
     if(Array.isArray(arr[i])){
         filterArray.push(...steamrollArray(arr[i]));
     } else{
         filterArray.push(arr[i]);
     }
 }
 return filterArray;
}

