function convertToRoman(num) {
 var ref= [
     ["M",1000],["CM",900],["D",500],
     ["CD",400],["C",100],["XC",90],
     ["L",50],["XL",40],["X",10],
     ["IX",9],["V",5],["IV",4],
     ["I",1]];

     var res = [];
     ref.forEach(function(Px){
         while(num >= Px[1]){
             res.push(Px[0]);
             num -= Px[1];
         }
     });
     return res.join("");
}

