class Thermostat {
  constructor(author) {
    this._author = (5/9) * (author - 32) ;
  }
  // getter
  get Thermostat(){
    return this._author;
  }
  // setter
  set Thermostat(updatedAuthor){
    this._author = updatedAuthor;
  }
}

  /* Alter code above this line */
  return Thermostat;
}
const Thermostat = makeClass();
