function diffArray(arr1, arr2) {
  var newArr = arr1.concat(arr2);
  var answer = newArr.filter(function(value){  
      if(arr1.indexOf(value)<0||arr2.indexOf(value)<0){
          return value;
      }
  });
  // Same, same; but different.
  return answer;
}

